import React, { useState } from 'react';
import { LoadingOutlined, PlusOutlined } from '@ant-design/icons';
import {Col, message, Row, Table, Upload, Image} from 'antd';

const getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result));
    reader.readAsDataURL(img);
};
const beforeUpload = (file) => {
    const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
    if (!isJpgOrPng) {
        message.error('You can only upload JPG/PNG file!');
    }
    const isLt2M = file.size / 1024 / 1024 < 2;
    if (!isLt2M) {
        message.error('Image must smaller than 2MB!');
    }
    return isJpgOrPng && isLt2M;
};
function StudentImgFind(props) {
    const [loading, setLoading] = useState(false);
    const [imageUrl, setImageUrl] = useState();
    const [resultList, setresultList] = useState();
    const [userList, setuserList] = useState([])
    const handleChange = (info) => {
        if (info.file.status === 'uploading') {
            setLoading(true);
            return;
        }
        if (info.file.status === 'done') {
            // Get this url from response in real world.
            getBase64(info.file.originFileObj, (url) => {
                setLoading(false);
                setImageUrl(url);
            });
            console.log("比对图片结果：",info.file.response)
            setresultList("data:image/jpeg;base64,"+info.file.response)
        }
    };
    const uploadButton = (
        <div>
            {loading ? <LoadingOutlined /> : <PlusOutlined />}
            <div
                style={{
                    marginTop: 8,
                }}
            >
                Upload
            </div>
        </div>
    );
    // 学号，姓名，性别，班级，手机号码，邮件地址。
    const columns = [
        {
            title: '学号',
            dataIndex: 'sid',
            key: 'sid',
            render: (text) => <a>{text}</a>,
        },
        {
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        },
        {
            title: '性别',
            dataIndex: 'gender',
            key: 'gender',
        },
        {
            title: '班级',
            dataIndex: 'classname',
            key: 'classname',
            render: (text) => <a>{text}</a>,
        },
        {
            title: '手机号码',
            dataIndex: 'mobile',
            key: 'mobile',
        },
        {
            title: '邮件地址',
            dataIndex: 'email',
            key: 'email',
        }
    ];
    return (
        <>
            <Row>
                <Col span={10}>
                    <h2>请选择被查找者的照片：</h2>
                </Col>
                <Col span={8}>
                    <Upload
                        name="avatar"
                        listType="picture-card"
                        className="avatar-uploader"
                        showUploadList={false}
                        action="http://localhost:8080/student-manage/imgQuery"
                        beforeUpload={beforeUpload}
                        onChange={handleChange}
                    >
                        {imageUrl ? (
                            <img
                                src={imageUrl}
                                alt="avatar"
                                style={{
                                    width: '100%',
                                }}
                            />
                        ) : (
                            uploadButton
                        )}
                    </Upload>
                </Col>
                <Col span={5}></Col>

                <Col span={9}>
                    <h2>查询结果：</h2>
                </Col>
                <Col span={8}>
                    {resultList ? (
                        <img
                            src={resultList}
                            alt="avatar"
                            style={{
                                width: '40%',
                            }}
                        />
                    ) : (
                        <Image
                            width={200}
                            height={200}
                            src="error"
                            fallback="data:image/png;base64,"
                        />
                    )}
                </Col>
                <Col span={5}></Col>
            </Row>


            {/*<Table*/}
            {/*    columns={columns}*/}
            {/*    pagination={{*/}
            {/*        position: ['bottomCenter'],*/}
            {/*    }}*/}
            {/*    dataSource={userList}*/}
            {/*/>*/}
        </>
    );
}

export default StudentImgFind;